import './Background.css';
import { Stage, Sprite, Container, Bunny } from '@inlet/react-pixi'
import { useReducer, useTick, useRef } from 'react';
import { KawaseBlurFilter } from "https://cdn.skypack.dev/@pixi/filter-kawase-blur@3.2.0";
import SimplexNoise from "https://cdn.skypack.dev/simplex-noise@3.0.0";
import hsl from "https://cdn.skypack.dev/hsl-to-hex";
import debounce from "https://cdn.skypack.dev/debounce";

function random(min, max) {
  return Math.random() * (max - min) + min;
}

const wizard =
  "https://s3-us-west-2.amazonaws.com/s.cdpn.io/693612/IaUrttj.png";

let i = 0;

const Wizard = () => {
  const [x, setX] = useState(0);
  const [y, setY] = useState(0);

  useTick((delta) => {
    i += 0.05 * delta;
    setX(Math.sin(i) * 100);
    setY(Math.sin(i / 1.5) * 100);
  });

  return <Sprite image={wizard} x={x} y={y} anchor={0.5} scale={1.3} />;
};

export function Background() {
    return (
        <Stage
            width={300}
            height={300}
            options={{
            backgroundColor: 0x012b30,
            antialias: true
            }}
        >
            <Wizard />
        </Stage>
    );
  }
